      const withCurlyBraces = text.split(HTML_ENTITY_REGEX()).map(word => (
        word === '' ? '' : `{${JSON.stringify(word)}}`
      )).join(HTML_ENTITY);

      const htmlEntities = text.match(HTML_ENTITY_REGEX());
